var debugOn = true;

const resultFields = {
	temperature: { min: -40, max: 100 },
	humidity: { min: 0, max: 100 },
	pressure: { min: 850, max: 1100 },
	raintotal: { min: 0, max: null }, // m3
	rainrate: { min: 0, max: 50 }, // mm
	direction: { min: 0, max: 360 },
	currentspeed: { min: 0, max: 200 }, // km/h
	averagespeed: { min: 0, max: 200 }, // km/h
	uvindex: { min: 0, max: 45 },
	brightness: { min: 0, max: 100000 } // lux
};

class WeatherSignal {
	constructor(object) {
		this.data = object;
		// Statistic counters
		this.count = 0;
		this.ok = 0;
		this.error = 0;
		this.invalid = 0;
		this.lengths = {};
		// Last message
		this.payload = [];
		this.result;
		this.debuginfo = [];
		WeatherSignal.signals.set(object.id, this);
	}
	// get: get a list of all signals or the WeatherSignal object
	static get(x) {
		return (x === undefined ? Array.from(WeatherSignal.signals.keys()) : WeatherSignal.signals.get(x))
	}
	// getName: get the descriptive name of the signal
	getName() { return this.data.name }
	// getSignal: get the name of the Homey signal definition
	getSignal() { return { def: this.data.signal, freq: this.data.freq || 433 } }
	// getHint: get more information on the signal support
	getHint(locale) { return (this.data.hint !== undefined ? this.data.hint[locale] : undefined) }
	// parse: parse the data and return the result
	parse(data) {
		let original = Object.prototype.toString.call(data) === '[object Array]' ? Array.from(data) : Array.from(data.values()); // type changed in FW 1.2.0
		let result = this.data.parser(data);
		let now = new Date();
		let valid = false;
		this.count++;
		if (result != null && typeof result !== 'string') {
			this.result = result;
			this.result.protocol = this.data.id;
			this.result.lastupdate = now;
			if (this.validate(this.result)) {
				this.ok++;
				let len = data.length;
				this.lengths[len] = (this.lengths[len] || 0) + 1;
				valid = true;
			} else {
				this.invalid++;
			}
		} else {
			this.debug('Error:', result);
			this.error++;
		}
		this.payload.push({
			data: original.join(','),
			valid: valid,
			when: now
		});
		if (this.payload.length > 10) {
			this.payload.shift();
		}
		return valid;
	}
	// validate: validate the received result
	validate(result) {
		let valid = false;
		if (result != null && result.data != null) {
			valid = true;
			for (let field in result.data) {
				let check = resultFields[field];
				if (check != null) {
					let min_err = (check.min != null ? result.data[field] < check.min : false);
					let max_err = (check.max != null ? result.data[field] > check.max : false);
					if (min_err || max_err) {
						this.debug('Value out of range:', result.data[field], '(' + field + ')');
						valid = false;
					}
				}
			}
		}
		return valid;
	}
	// getResult: read the last result
	getResult() { return this.result }
	// getStatistics: return the error rate for this signal
	getStatistics() {
		return {
			total: this.count,
			ok: ((this.ok / this.count) * 100 || 0).toFixed() + '%',
			error: ((this.error / this.count) * 100 || 0).toFixed() + '%',
			invalid: ((this.invalid / this.count) * 100 || 0).toFixed() + '%',
			lengths: this.lengths,
			payloads: this.payload,
			debug: this.debuginfo,
			last: this.result
		}
	}
	// debug: debug specific for this ID
	debug() {
		let id = '[' + this.data.id + ']';
		let result = debug(id, arguments);
		if (result !== undefined) {
			 // Remove fixed id & make more readable JSON with single quote
			let msg = result.msg.slice(id.length + 1).replace(/"/g, "'");
			this.debuginfo.push('[' + result.time + '] ' + msg);
			if (this.debuginfo.length > 25) {
				this.debuginfo.shift();
			}
		}
	}
}
// Wait for ES7 before we can move this into the class
WeatherSignal.signals = new Map();

// Debug logging
function debug() {
	if (debugOn) {
		var now = new Date();
		var itemdebug = (x) => {
			var t = Object.prototype.toString.call(x);
			return (t === '[object Object]' ? JSON.stringify(x, null, 3) : (typeof x === 'object' ? JSON.stringify(x) : x)) + ' '
		};
		var time = now.toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, "$1");
		var ms = ('00' + now.getMilliseconds()).slice(-3);
		var text = '';
		for (var i = 0; i < arguments.length; i++) {
			var x = arguments[i];
			if (Object.prototype.toString.call(arguments[i]) === '[object Arguments]') {
				for (var j = 0; j < x.length; j++) {
					text += itemdebug(x[j]);
				}
			} else {
				text += itemdebug(x);
			}
		}
		time += '.' + ms;
		console.log(time, text.slice(0, -1));
		return { time: time, msg: text.slice(0, -1) }
	}
}

module.exports = {
	WeatherSignal,
	debug,
	setDebug: function(on) { debugOn = on },
	bin2dec: (x) => parseInt(x, 2).toString(10),
	bin2hex: (x) => parseInt(x, 2).toString(16),
	hex2dec: (x) => parseInt(x, 16).toString(10),
	dec2hex: (x) => parseInt(x, 10).toString(16)
};
